import React from "react";
// import FooterMenu from "../components/FooterMenu; ";

//style
import '../components/styleSheets/sk.css';

const Home = () => {

    const [bgImage, setBgImage] = useState("");

    useEffect(() => {
        const updateBackground = () => {
            if (window.innerWidth < 768) {
                setBgImage("https://greattakes.s3.us-east-2.amazonaws.com/sk/mobile_BG.png");
            } else {
                setBgImage("https://greattakes.s3.us-east-2.amazonaws.com/sk/web_BG.png");
            }
        };

        updateBackground(); // Set initial background
        window.addEventListener("resize", updateBackground);

        return () => window.removeEventListener("resize", updateBackground);
    }, []);


    return (
        <div
            className="w-full h-screen bg-cover bg-center transition-all duration-300"
            style={{ backgroundImage: `url(${bgImage})` }}
        >
            <div className="flex items-center justify-center h-full text-white text-2xl font-bold">
                Responsive Background
            </div>
        </div>
    );

};

export default Home;
